
 CREATE TABLE users (
    ID BIGSERIAL,
    username text,
    password text,
    email varchar(128)
 );



CREATE TABLE  warehouse(
    ID BIGSERIAL,
    name text,
    parentId BIGSERIAL,
    PRIMARY KEY (ID),
    FOREIGN KEY (parentId) REFERENCES warehouse (ID)
);

CREATE TABLE BrandAuto(
    ID BIGSERIAL,
    name text,
    PRIMARY KEY (ID)
);

CREATE TABLE ModelAuto(
    ID BIGSERIAL,
    IdBrand BIGSERIAL,
    name text,
    PRIMARY KEY (ID),
    FOREIGN KEY (IdBrand) REFERENCES BrandAuto (ID)
);

CREATE TABLE items(
    ID BIGSERIAL,
    IdModelAuto BIGSERIAL,
    name text,
    description text,
    pret text,
    imageURL text,
    cantitate text,
    PRIMARY KEY (ID),
    FOREIGN KEY (IdModelAuto) REFERENCES ModelAuto (ID)
);

CREATE TABLE items(
    ID BIGSERIAL,
    IdModelAuto BIGSERIAL,
    name text,
    description text,
    pret integer,
    imageURL text,
    cantitate integer,
    PRIMARY KEY (ID),
    FOREIGN KEY (IdModelAuto) REFERENCES ModelAuto (ID)
);

